insert into lecture(class_code, user_code, major, department, class_name, classification, credit, classroom, information) VALUES('WRHW3743','8','기계공학과','공과대학','기계공학의이해','선택전공','3','공학관302호','이 교과목은 공학의 발달사, 기계공학의 범위, 기계공학의 각 분야별 내용 및 특성의 개략적인 소개를 통하여 공학과 기계공학에 대해 이해하도록 강의한다.');
insert into lecture(class_code, user_code, major, department, class_name, classification, credit, classroom, information) VALUES('REDH6234','9','전기전자공학과','공과대학','전기전자공학','필수전공','3','공학관102호','본 강의에서는 자연계에서 발생하는 현상 중 전자와 관련한 내용을 다루는 분야로 전기전자공학의 기초 회로이론, 회로구성요소(R, L, C), 증폭기, 반도체 소자를 이용한 회로의 구성 방법 및 특성을 이해을 이해하고자 한다.');
insert into lecture(class_code, user_code, major, department, class_name, classification, credit, classroom, information) VALUES('HERG2633','10','국제통상학과','경영대학','국제통상의 이해','선택전공','3','인문관402호','국제통상의 전체적 이해를 위한 기초 과목으로 근대화의 역사, 세계화를 포괄적으로 이해한다. 국제무역의 이론과 정책, 통상협상, 국제경영의 다양한 해석을 기초 수준에서 배운다.');
insert into lecture(class_code, user_code, major, department, class_name, classification, credit, classroom, information) VALUES('RJTR6237','11','경제학과','경영대학','경제학원론1','전공필수','3','인문관102호','경제이론의 전 분야를 소개하고 기초적인 경제이론을 미시경제학과 거시경제학으로 구분하여 경제학을 처음 배우는 학생들의 경제학에 대한 체계확립과 폭넓은 이해력과 통찰력을 길러 이론 전개력을 가질 수 있도록 한다.');
insert into lecture(class_code, user_code, major, department, class_name, classification, credit, classroom, information) VALUES('HUKL3674','12','마케팅학과','경영대학','마케팅의 이해','필수전공','3','교육관602호','본 과목은 사회과학 분야에서 다양한 형태로 진행되는 마케팅 관련 과목들의 선수과목으로서 성격을 갖고 있다. 따라서 마케팅에 대해서 잘 모르는 학생들이 교양 관점에서 개념에 대한 이해와 기초적인 활용에 관점을 두고 강의가 진행이 된다. 본 과목을 이수함으로써 학생들은 각 학과에서 기업과 관련되어 진행되는 경영활동의 하나인 마케팅에 대한 이해, 학과에 따라서 마케팅 관련 과목이 있는 경우 개괄적인 마케팅에 대한 이해를 하는데 도움이 된다. 학생들은 본 과목을 듣고 학과에서 진행되는 마케팅 관련 과목들을 듣는다면 체계적인 마케팅 지식을 함양할 수 있을 것이다.');
insert into lecture(class_code, user_code, major, department, class_name, classification, credit, classroom, information) VALUES('SBER2458','13','호텔관광경영학과','경영대학','호텔관광마케팅','필수전공','3','인문관402호','현대 마케팅관리의 제 기법을 바탕으로 관광시장의 형태, 규모, 조사방법, 시장계획, 시장선전을 행할 수 있는 이론적 기초를 체계적으로 연구하며 일반 마케팅 이론을 산업의 특수성과 연관 지어서 호텔업 측면에서의 시장, 고객, 경쟁자의 조사분석방법을 연구하고 과학적 예측 및 대책수립 능력을 배양한다.');
insert into lecture(class_code, user_code, major, department, class_name, classification, credit, classroom, information) VALUES('SRHR7345','14','정보시스템경영학과','경영대학','경영정보시스템','선택전공','3','교육관302호','기업에서 활용되는 정보기술에 대한 이해를 바탕으로 정보기술과 경영전략과의 관계를 이해한다.');
insert into lecture(class_code, user_code, major, department, class_name, classification, credit, classroom, information) VALUES('DFGS3464','15','미디어커뮤니케이션학과','경영대학','방송 원론','필수전공','3','교육관702호','<방송원론>은 방송이 지닌 기본적인 속성과 함께 변해가는 모습을 함께 알아보는 목적을 갖는다. 하지만 방송에 대한 이해가 강의 목적 전부는 아니다. 방송의 세계를 알아보는 것을 넘어서서 방송계, 방송 산업은 과연 나의 일상과 어떤 관계를 맺는지, 미디어 산업에 종사할 준비를 하는 나는 방송에 대해 어떤 태도를 지닐 지를 고민하는 목적도 동시에 지닌다. 방송이 다른 매체와 갖는 관계를 어떻게 이해해야 하는가까지 고민하며 미디어 생태계를 정리하는 목적도 갖는다. 뿐만 아니라 언론학과 사회이론, 문화이론 등을 방송과 접목시킴으로써 방송의 사회성을 체계화하여 학문적으로 설명하는 이론들을 숙지하는 기회도 가질 것이다.');
insert into lecture(class_code, user_code, major, department, class_name, classification, credit, classroom, information) VALUES('WGEA1612','16','영어영문학과','인문대학','영문학개론','선택전공','3','벤쳐관505호','This course introduces the background and the beginnings of English literature and a chronological genre surbey of English literature with reference to major authors.');
insert into lecture(class_code, user_code, major, department, class_name, classification, credit, classroom, information) VALUES('ERHR1633','17','철학과','인문대학','글로벌인문학','선택전공','3','벤쳐관105호','이 강좌는 대학생들이 갖추어야 할 기본적인 인문학적 소양을 함양시키는데 그 목적이 있다. 이 강좌는 인간의 본성에 대한 깊이 있는 이해, 사회와 세계에 대한 포괄적 성찰, 공유 가능한 인류 문명의 자산에 대한 사유체험, 과거와 미래, 전통과 역사에 대한 성찰적 전망, 자신을 표현할 수 있는 논리력과 표현능력, 지구촌의 사람들과 보편적 가치를 가지고 소통할 수 있는 소통능력, 지구촌에서 인류애(humanitas)를 실현할 수 있는 실천능력 등을 계발하는데 그 목적이 있다. 이를 위해 매 학기 새로운 하나의 대(大) 주제가 설정되고 그 아래 여러 가지 하부주제들이 다루어지고, 그 주제는 철학, 역사, 사회, 정치, 문학, 예술, 문화, 종교, 문명, 자연과학, 인간본성, 동서정신세계의 비교 등 다양한 영역에서 포괄적으로 다루어질 것이다.');
insert into lecture(class_code, user_code, major, department, class_name, classification, credit, classroom, information) VALUES('SHRR2463','18','사학과','인문대학','한국철학사','선택전공','3','벤쳐관205호','한국에서의 철학적 사유의 전개과정에 대한 소개');
insert into lecture(class_code, user_code, major, department, class_name, classification, credit, classroom, information) VALUES('ERHE2363','19','신문방송학과','인문대학','신문방송학개론','필수전공','3','벤쳐관305호','이 수업은 커뮤니케이션 현상의 본질에 대한 이해를 도모함으로써 사회 속에서 나타나는 다양한 커뮤니케이션의 양태와 속성을 파악하기 위한 기본 지식의 함양을 기본적인 목표로 한다. 구체적으로 커뮤니케이션의 본질에 대한 고민, 좋은 커뮤니케이터가 갖추어야 할 조건과 커뮤니케이션 유형, 매스미디어에 대한 이해, 메시지의 특성 및 해독 등에 대해 다룬다.');
insert into lecture(class_code, user_code, major, department, class_name, classification, credit, classroom, information) VALUES('HASS6132','20','언어인지과학과','인문대학','언어와 인지','선택전공','3','벤쳐관405호','언어와 인지>는 언어의 내재적 특징과 인지구조의 관계를 인지과학적인 견지에서 검토한다. 첫째, 언어 정보를 이해하고 발화하는 언어 처리의 기제를 언어심리학과 인지과학의 측면에서 논의하고, 특히 내러티브의 총체적, 지엽적 구조가 인지과학의 이론에 의해 어떤 관계를 갖는지 대한 논의를 한다. 둘째, 언어를 습득하거나, 장애를 겪는 인간의 인지적 경험이 마음 (mind/brain) 속에 어떻게 발생하는지에 대해 신경과학적인 관점에서 검토하고, 셋째, 그러한 경험을 가능하게 하는 언어능력(language faculty)이 어떠한 표상 구조로 되어 있는지에 대해 현대언어학, 인지언어학, 문화인류학의 상호보완적 측면에서 검토한다.');
